using System;
using System.Data;
using System.Configuration;
using System.Collections;
using System.Web;
using System.Web.Security;
using System.Web.UI;
using System.Web.UI.WebControls;
using System.Web.UI.WebControls.WebParts;
using System.Web.UI.HtmlControls;
using ICSSoft.STORMNET.Business;
using ICSSoft.STORMNET.Business.LINQProvider;
using System.Linq;
using System.Collections.Generic;

namespace IIS.Склад
{
    public partial class Запрос3 : System.Web.UI.Page
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            var ds = (SQLDataService)DataServiceProvider.DataService;
            var товары = ds.Query<Товар>(Товар.Views.ТоварL).ToList();
            var всеТоварыНаСкладах = ds.Query<ТоварНаСкладе>(ТоварНаСкладе.Views.ТоварНаСкладеE).ToList();
            var результат = new List<ТоварНаСкладе>();
            foreach (var товар in товары)
            {
                var товарыНаСкладе = всеТоварыНаСкладах.Where(x => x.Товар.__PrimaryKey.Equals(товар.__PrimaryKey)).ToList();
                if (товарыНаСкладе.Count() > 1)
                {
                    результат.Add(товарыНаСкладе.FirstOrDefault());
                }
            }
            dataList.DataSource = результат;
            dataList.DataBind();
        }
    }
}
