using System;
using System.Data;
using System.Configuration;
using System.Collections;
using System.Web;
using System.Web.Security;
using System.Web.UI;
using System.Web.UI.WebControls;
using System.Web.UI.WebControls.WebParts;
using System.Web.UI.HtmlControls;
using ICSSoft.STORMNET.Business;
using ICSSoft.STORMNET.Business.LINQProvider;
using System.Linq;
using System.Collections.Generic;

namespace IIS.Склад
{
    public partial class Запрос4 : System.Web.UI.Page
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            var ds = (SQLDataService)DataServiceProvider.DataService;
            var склады = ds.Query<Склад>(Склад.Views.СкладL).ToList();
            var всеТоварыНаСкладах = ds.Query<ТоварНаСкладе>(ТоварНаСкладе.Views.ТоварНаСкладеE).ToList().
                OrderBy(x => x.Склад.Название).ThenBy(x => x.Товар.Наименование);
            List<СкладИОбщаяСумма> складИОбщаяСумма = new List<СкладИОбщаяСумма>();
            if (склады != null)
            {
                foreach (var склад in склады)
                {
                    var товарыНаСкладе = всеТоварыНаСкладах.Where(x => x.Склад.__PrimaryKey.Equals(склад.__PrimaryKey)).ToList();
                    var сумма = товарыНаСкладе.Select(x => x.Количество).Sum();

                    var y = new СкладИОбщаяСумма { Склад = склад.Название, СуммаТоваров = сумма };
                    складИОбщаяСумма.Add(y);
                }
                dataList.DataSource = всеТоварыНаСкладах;
                dataList.DataBind();
                dataList1.DataSource = складИОбщаяСумма;
                dataList.DataBind();

            }
        }
    }
    class СкладИОбщаяСумма
    {
        public string Склад { get; set; }
        public double СуммаТоваров { get; set; }
    }
}
