using System;
using System.Data;
using System.Configuration;
using System.Collections;
using System.Web;
using System.Web.Security;
using System.Web.UI;
using System.Web.UI.WebControls;
using System.Web.UI.WebControls.WebParts;
using System.Web.UI.HtmlControls;
using ICSSoft.STORMNET.Business;
using ICSSoft.STORMNET.Business.LINQProvider;
using System.Linq;

namespace IIS.Книги
{
    public partial class Query5 : System.Web.UI.Page
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            WOLV.DataObjectTypes = new[] { typeof(Книга) };
            WOLV.View = Книга.Views.КнигаL;
            WOLV.Operations.New = false;
            WOLV.Operations.Edit = false;
            WOLV.Operations.EditInRow = false;
            WOLV.Operations.Delete = false;
            WOLV.Operations.DeleteInRow = false;
            //WOLV.TopPagerVisible = false;
            var ds = (SQLDataService)DataServiceProvider.DataService;
            if (Session["aver"] != null)
            {
                var aver = Convert.ToInt32(Session["aver"]);
                var books = ds.Query<Книга>(Книга.Views.КнигаL).Where(b => b.КоличествоСтраниц > aver);
                WOLV.LimitFunction = LinqToLcs.GetLcs(books.Expression, Книга.Views.КнигаL).LimitFunction;
                Label3.Text = aver.ToString();
            }
        }

        protected void Button1_Click(object sender, EventArgs e)
        {
            try
            {
                var ds = (SQLDataService)DataServiceProvider.DataService;
                var aver = ds.Query<Книга>(Книга.Views.КнигаL).ToList().Average(b => b.КоличествоСтраниц);
                Session["aver"] = aver;
                //Label3.Text = aver.ToString();
            }
            catch (Exception)
            {

                Session["aver"] = null;
            }
            Response.Redirect(Request.Url.ToString());
        }
    }
}
