using System;
using System.Data;
using System.Configuration;
using System.Collections;
using System.Web;
using System.Web.Security;
using System.Web.UI;
using System.Web.UI.WebControls;
using System.Web.UI.WebControls.WebParts;
using System.Web.UI.HtmlControls;
using ICSSoft.STORMNET.Business;
using ICSSoft.STORMNET.Business.LINQProvider;
using ICSSoft.STORMNET.FunctionalLanguage.SQLWhere;
using System.Linq;
using ICSSoft.STORMNET.Web.AjaxControls.Forms;

namespace IIS.Lectures
{
    public partial class Запрос2 : BasePage
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            var ds = (SQLDataService)DataServiceProvider.DataService;

            if (!IsPostBack)
            {
                var subjects = ds.Query<Тетрадь>(Тетрадь.Views.ТетрадьL.Name);
                foreach (var subject in subjects)
                    dropDownList2.Items.Add(subject.Название);
            }
        }

        protected void button2_Click(object sender, EventArgs e)
        {
            var ds = (SQLDataService)DataServiceProvider.DataService;

            var subjects = ds.Query<Тетрадь>(Тетрадь.Views.ТетрадьE)
                    .Where(t => t.Название == dropDownList2.SelectedValue)
                    .First()
                    .ПредметВТетради.Cast<ПредметВТетради>()
                    .OrderBy(t => t.Предмет.Название).ToList()
                    .Select(t => t.Предмет.Название).Distinct();

            TableRow tr;
            TableCell tc;
            int i = 0;

            foreach (var subject in subjects)
            {
                i++;
                tr = new TableRow();

                tc = new TableCell();
                tc.Controls.Add(new LiteralControl(i + "."));
                tr.Cells.Add(tc);

                tc = new TableCell();
                tc.Controls.Add(new LiteralControl(subject));
                tr.Cells.Add(tc);

                table2.Rows.Add(tr);
            }
        }
    }


}
