namespace IIS.Lectures
{
    public partial class Запрос3 : BasePage
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            var ds = (SQLDataService)DataServiceProvider.DataService;

            var colors = ds.Query<Тетрадь>(Тетрадь.Views.ТетрадьL.Name)
                .OrderBy(t => t.ЦветОбложки).ToList()
                .Select(c => c.ЦветОбложки).Distinct();

            TableRow tr;
            TableCell tc;
            int i = 0;

            foreach (var color in colors)
            {
                i++;
                tr = new TableRow();

                tc = new TableCell();
                tc.Controls.Add(new LiteralControl(i + "."));
                tr.Cells.Add(tc);

                tc = new TableCell();
                tc.Controls.Add(new LiteralControl(color));
                tr.Cells.Add(tc);

                table1.Rows.Add(tr);
            }
        }
    }
}
