namespace IIS.Электронный_Магазин
{
    public partial class Запрос2 : BasePage
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            var ds = (SQLDataService)DataServiceProvider.DataService;
            var mon = DateTime.Today.Month - 1;
            var q = ds.Query<Платёжка>(Платёжка.Views.ПлатёжкаL).Where(a=>a.Дата.Month==mon).ToList().Select(c=>new { Details = c.СтрокаИзПлатёжки}).ToList();
            var tov = ds.Query<Товар>(Товар.Views.ТоварL);
            foreach (var t in tov)
            {
                bool bo = true;
                foreach (var kl in q)
                {
                    int i = 0;
                    while((i < kl.Details.Count) &&(bo))
                   //for(var i=0;i<kl.Details.Count;i++)
                    { 
                            if (kl.Details[i].Товар.Наименование == t.Наименование)
                                bo = false;
                        i++;
                    }
                }
                if (bo)
                    Bull.Items.Add(t.Наименование);
            }
        }
    }
}
